import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MatchList = ({ onMatchSelect }) => {
    const [matches, setMatches] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                setLoading(true);
                setError(null);
                const response = await axios.get('http://localhost:5000/api/matches');
                if (response.data.status === 'success') {
                    setMatches(response.data.matches);
                } else {
                    setError('Failed to fetch matches: ' + response.data.message);
                }
            } catch (err) {
                setError('Failed to connect to the server. Please ensure the backend is running.');
                console.error('API Error:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchMatches();
    }, []);

    if (loading) return (
        <div className="match-list">
            <div className="loading-spinner">Loading matches...</div>
        </div>
    );

    if (error) return (
        <div className="match-list">
            <div className="error-message">{error}</div>
            <button 
                className="retry-button"
                onClick={() => window.location.reload()}
            >
                Retry
            </button>
        </div>
    );

    return (
        <div className="match-list">
            <h2>Upcoming Matches</h2>
            {matches.length === 0 ? (
                <div className="no-matches">No upcoming matches found</div>
            ) : (
                <div className="matches">
                    {matches.map(match => (
                        <div 
                            key={match.match_id} 
                            className="match-card"
                            onClick={() => onMatchSelect(match.match_id)}
                        >
                            <div className="teams">
                                <span className="team">{match.team1}</span>
                                <span className="vs">vs</span>
                                <span className="team">{match.team2}</span>
                            </div>
                            <div className="match-details">
                                <span className="date">{new Date(match.date).toLocaleDateString()}</span>
                                <span className="venue">{match.venue}</span>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default MatchList;
