import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MatchPredictions = ({ matchId }) => {
    const [predictions, setPredictions] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPredictions = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/predictions/${matchId}`);
                setPredictions(response.data.predictions);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch predictions');
                setLoading(false);
            }
        };

        if (matchId) {
            fetchPredictions();
        }
    }, [matchId]);

    if (loading) return <div>Loading predictions...</div>;
    if (error) return <div>Error: {error}</div>;
    if (!predictions) return <div>Select a match to view predictions</div>;

    return (
        <div className="match-predictions">
            <h2>Match Predictions</h2>
            <div className="match-header">
                <h3>{predictions.team1.name} vs {predictions.team2.name}</h3>
                <p>Venue: {predictions.venue}</p>
                <p>Date: {new Date(predictions.date).toLocaleDateString()}</p>
            </div>

            <div className="teams-predictions">
                {['team1', 'team2'].map(teamKey => (
                    <div key={teamKey} className="team-predictions">
                        <h3>{predictions[teamKey].name}</h3>
                        <div className="players">
                            {predictions[teamKey].players.map(player => (
                                <div key={player.name} className="player-card">
                                    <h4>{player.name}</h4>
                                    <div className="prediction">
                                        <p>Predicted Runs: {player.prediction.runs.toFixed(1)}</p>
                                        <p>Predicted Wickets: {player.prediction.wickets.toFixed(1)}</p>
                                        <p>Confidence: {(player.prediction.confidence * 100).toFixed(1)}%</p>
                                    </div>
                                    <div className="stats">
                                        <h5>Recent Performance</h5>
                                        <p>Average Runs: {player.recent_performance.avg_runs.toFixed(1)}</p>
                                        <p>Average Wickets: {player.recent_performance.avg_wickets.toFixed(1)}</p>
                                        <p>Form: {(player.recent_performance.form * 100).toFixed(1)}%</p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MatchPredictions;
