import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MatchList = ({ onMatchSelect }) => {
    const [matches, setMatches] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/matches');
                setMatches(response.data.matches);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch matches');
                setLoading(false);
            }
        };

        fetchMatches();
    }, []);

    if (loading) return <div>Loading matches...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="match-list">
            <h2>Upcoming Matches</h2>
            <div className="matches">
                {matches.map(match => (
                    <div 
                        key={match.match_id} 
                        className="match-card"
                        onClick={() => onMatchSelect(match.match_id)}
                    >
                        <div className="teams">
                            <span className="team">{match.team1}</span>
                            <span className="vs">vs</span>
                            <span className="team">{match.team2}</span>
                        </div>
                        <div className="match-details">
                            <span className="date">{new Date(match.date).toLocaleDateString()}</span>
                            <span className="venue">{match.venue}</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MatchList;
